function App() {
  const [rates, setRates] = useState([]);
  const [quantity1, setQuantity1] = useState(1);
  const [quantity2, setQuantity2] = useState(1);
  const [quantity3, setQuantity3] = useState(1);
  const [currency1, setCurrency1] = useState('EUR');
  const [currency2, setCurrency2] = useState('UAH');
  const [currency3, setCurrency3] = useState('PLN');

  const handleCurrencyChange = (currency, index) => {
    switch (index) {
      case 1:
        setCurrency1(currency);
        setQuantity2(convertCurrency(quantity1, rates[currency1], rates[currency2], currency));
        setQuantity3(convertCurrency(quantity1, rates[currency1], rates[currency3], currency));
        break;
      case 2:
        setCurrency2(currency);
        setQuantity1(convertCurrency(quantity2, rates[currency2], rates[currency1], currency));
        setQuantity3(convertCurrency(quantity2, rates[currency2], rates[currency3], currency));
        break;
      case 3:
        setCurrency3(currency);
        setQuantity1(convertCurrency(quantity3, rates[currency3], rates[currency1], currency));
        setQuantity2(convertCurrency(quantity3, rates[currency3], rates[currency2], currency));
        break;
      default:
        break;
    }
  };

const handleQuan1 = (quantity, currency) => {
  setQuantity1(quantity);
  setCurrency1(currency);
  const newRates = { ...rates };
  newRates[currency1] = 1;
  setRates(newRates);
  setQuantity2(convertCurrency(quantity, rates[currency], rates[currency2], currency));
  setQuantity3(convertCurrency(quantity, rates[currency], rates[currency3], currency));
  };

  const handleQuan2 = (quantity, currency) => {
  setQuantity2(quantity);
  setCurrency2(currency);
  const newRates = { ...rates };
  newRates[currency2] = 1;
  setRates(newRates);
  setQuantity1(convertCurrency(quantity, rates[currency], rates[currency1], currency));
  setQuantity3(convertCurrency(quantity, rates[currency], rates[currency3], currency));
  };

  const handleQuan3 = (quantity, currency) => {
  setQuantity3(quantity);
  setCurrency3(currency);
  const newRates = { ...rates };
  newRates[currency3] = 1;
  setRates(newRates);
  setQuantity1(convertCurrency(quantity, rates[currency], rates[currency1], currency));
  setQuantity2(convertCurrency(quantity, rates[currency], rates[currency2], currency));
  };

  useEffect(() => {
  const fetchData = async () => {
  const result = await axios.get('https://api.exchangerate-api.com/v4/latest/EUR');
  setRates(result.data.rates);
  };
  fetchData();
  }, []);

  const convertCurrency = (value, fromRate, toRate, toCurrency) => {
  const eurValue = value / fromRate;
  const convertedValue = eurValue * toRate;
  return parseFloat(convertedValue.toFixed(2));
  };

  return (
  <div className="container">
  <h1>Currency Converter</h1>
  <div className="row">
  <CurrencyInput
  currency={currency1}
  quantity={quantity1}
  onCurrencyChange={(currency) => handleCurrencyChange(currency, 1)}
  onQuantityChange={handleQuan1}
  />
  <CurrencyInput
  currency={currency2}
  quantity={quantity2}
  onCurrencyChange={(currency) => handleCurrencyChange(currency, 2)}
  onQuantityChange={handleQuan2}
  />
  <CurrencyInput
  currency={currency3}
  quantity={quantity3}
  onCurrencyChange={(currency) => handleCurrencyChange(currency, 3)}
  onQuantityChange={handleQuan3}
  />
  </div>
  </div>
  );
  }

  export default App;
