import { useEffect, useState } from 'react';
import './App.css';
import InputCurrency from './components/InputCurrency';
import axios from 'axios';
// import { response } from 'express';




function App() {
  const [rates, setRates] = useState([]);

  const [quantity1, setQuantity1] = useState(1);
  const [quantity2, setQuantity2] = useState(1);
  const [quantity3, setQuantity3] = useState(1);

  const [currency1, setCurrency1] = useState(1);
  const [currency2, setCurrency2] = useState(1);
  const [currency3, setCurrency3] = useState(1);


  const handleQuan1 = (quantity1) =>{
  setQuantity2(quantity1 * rates[currency2] / rates[currency1])
  setQuantity3(quantity1 * rates[currency3] / rates[currency1])
  setQuantity1(quantity1)
  }
  const handleCurr1 = (currency1) =>{
    setQuantity2(quantity1 * rates[currency2] / rates[currency1])
    setQuantity3(quantity1 * rates[currency3] / rates[currency1])
    setQuantity1(quantity1)
    }






useEffect(() => {
axios.get('https://api.freecurrencyapi.com/v1/latest?apikey=HsNDiuWgHsDZxtm4DjoraBreH804hau2zmUAVxkw&currencies=EUR%2CUSD%2CCAD')
.then( response =>{
setRates(response.data.data)
})
});

  return (
    <div className="App">
      <header className="App-header">
        <InputCurrency
        quantityChange={handleQuan1}
        currencyChange={setCurrency1}
          quantity={quantity1}
          currencies={Object.keys(rates)}
          currency={currency1}
        />
        <InputCurrency
        quantityChange={setQuantity2}
        currencyChange={setCurrency2}
          quantity={quantity2}
          currencies={Object.keys(rates)}
          currency={currency2}
        />
        <InputCurrency
        quantityChange={setQuantity3}
        currencyChange={setCurrency3}
          quantity={quantity3}
          currencies={Object.keys(rates)}
          currency={currency3}
        />
      </header>
    </div>
  );
}

export default App;
