import { useEffect, useState } from 'react';
import './App.css';
import InputCurrency from './components/InputCurrency';
import axios from 'axios';




function App() {
  const [rates, setRates] = useState([]);

  const [quantity1, setQuantity1] = useState(1);
  const [quantity2, setQuantity2] = useState(1);
  const [quantity3, setQuantity3] = useState(1);

  const [currency1, setCurrency1] = useState(1);
  const [currency2, setCurrency2] = useState(1);
  const [currency3, setCurrency3] = useState(1);


  const handleQuan1 = (quantity1) => {
    setQuantity2(quantity1 * rates[currency2] / rates[currency1])
    setQuantity3(quantity1 * rates[currency3] / rates[currency1])
    setQuantity1(quantity1)
  }
  const handleCurr1 = (currency1) => {
    setQuantity2(quantity1 * rates[currency2] / rates[currency1])
    setQuantity3(quantity1 * rates[currency3] / rates[currency1])
    setCurrency1(currency1)
  }

  const handleQuan2 = (quantity2) => {
    setQuantity1(quantity2 * rates[currency1] / rates[currency2])
    setQuantity3(quantity2 * rates[currency3] / rates[currency2])
    setQuantity2(quantity2)
  }
  const handleCurr2 = (currency2) => {
    setQuantity1(quantity2 * rates[currency1] / rates[currency2])
    setQuantity3(quantity2 * rates[currency3] / rates[currency2])
    setCurrency2(currency2)
  }

  const handleQuan3 = (quantity3) => {
    setQuantity1(quantity3 * rates[currency1] / rates[currency3])
    setQuantity2(quantity3 * rates[currency2] / rates[currency3])
    setQuantity3(quantity3)
  }
  const handleCurr3 = (currency3) => {
    setQuantity1(cutquantity3 * rates[currency1] / rates[currency3])
    setQuantity2(cutquantity3 * rates[currency2] / rates[currency3])
    setCurrency3(currency3)
  }


