  num.toFixed(4)
}



  useEffect(() => {
    axios.get('https://api.freecurrencyapi.com/v1/latest?apikey=HsNDiuWgHsDZxtm4DjoraBreH804hau2zmUAVxkw&currencies=EUR%2CUSD%2CCAD')
      .then(response => {
        setRates(response.data.data)
      })
  });

  return (
    <div className="App">
      <header className="App-header">
        <InputCurrency
          quantityChange={handleQuan1}
          currencyChange={handleCurr1}
          quantity={quantity1}
          currencies={Object.keys(rates)}
          currency={currency1}
        />
        <InputCurrency
          quantityChange={handleQuan2}
          currencyChange={handleCurr2}
          quantity={quantity2}
          currencies={Object.keys(rates)}
          currency={currency2}
        />
        <InputCurrency
          quantityChange={handleQuan3}
          currencyChange={handleCurr3}
          quantity={quantity3}
          currencies={Object.keys(rates)}
          currency={currency3}
        />
