import React, { useEffect, useState } from "react";
import PropTypes from 'prop-types'

const InputCurrency = (props) => {
  const [prevCurrency, setPrevCurrency] = useState('');

const handleCurrChange = (currency) => {
  setPrevCurrency(currency);
  props.currencyChange(currency);
}
  useEffect(() => {
    // Если выбранная валюта (props.currency) не равна предыдущей (prevCurrency),
    // то обновляем значения других валют с помощью переданной функции currencyChange
    if (prevCurrency !== props.currency) {
      props.currencyChange(props.currency);
    }
  }, [props.currency, prevCurrency, props.currencyChange]);
  return (
    <div className="customInput">
      <select value={props.currency} onChange={e=>props.currencyChange(e.target.value)}>
        {props.currencies.map((currency => (
          <option value={currency}>
            {currency}
          </option>
        )))}
      </select>
      <input type="number" onFocus={e => e.target.value = 1} value={props.quantity} onChange={e=>props.quantityChange(e.target.value)} />
      
    </div>
  );
};

InputCurrency.propTypes={
currencyQuantity: PropTypes.number.isRequired,
currency: PropTypes.string.isRequired,
currencies: PropTypes.array,
currencyChange:PropTypes.func,
quantityChange:PropTypes.func
}

export default InputCurrency;
